INSERT INTO templates VALUES (NULL,'2003-06-04 10:13:10','2003-06-23 20:39:02',3,0,'form','./themes/Gray_Matter/form.ihtml','<!-- $Id: templates.sql,v 1.37 2003/07/06 12:50:43 glen Exp $ -->\r\n<p>{form_instructions}</p>\r\n\r\n{!menu_item form_action \'{folder_name}\'\r\n  \'{site_path}/folder.php?id={folder_id}\'\r\n  \'{folder_id}+0\'!}\r\n{!menu_item form_action \'{doc_folder_name}\'\r\n  \'{site_path}/folder.php?id={doc_folder_id}\'\r\n  \'{doc_folder_id}+0\'!}\r\n{!menu_item form_action \'{doc_title}\'\r\n  \'{site_path}/document.php?id={doc_id}\'\r\n  \'{doc_id}+0\'!}\r\n<p class=\"action\">\r\n{!menu form_action \' {sep} \'!}\r\n</p>\r\n\r\n<form method=\"post\" action=\"{form_action}\" name=\"{form_name}\" enctype=\"multipart/form-data\" {form_java}>\r\n{BEGIN:input_form}\r\n<div class=\"formrow\">\r\n  {!if \'\"{input_form_type}\"==\"ignore\"\'\r\n      \'{input_form_prompt}\'\r\n      \'<span class=\"prompt\">{input_form_prompt}:</span>\'\r\n  !}\r\n  <span class=\"field\">\r\n  {input_form_field}\r\n  {!if defined(input_form_doc) \'<span class=\"doc\">{input_form_doc}</span>\'!}\r\n  </span>\r\n</div>\r\n{END:input_form}\r\n<input type=\"hidden\" name=\"submitted\" value=\"1\"/>\r\n{BEGIN:input_form_hidden}\r\n{hidden_form_field}\r\n{END:input_form_hidden}\r\n<div class=\"formrow\">\r\n  <span class=\"field\">\r\n  <input type=\"submit\" value=\"Submit\"/>\r\n  <input type=\"reset\"/>\r\n  </span>\r\n</div>\r\n&nbsp;\r\n</form>\r\n');
INSERT INTO templates VALUES (NULL,'2003-06-04 10:13:10','2003-06-04 10:13:10',3,0,'page','./themes/Gray_Matter/page.ihtml','<!DOCTYPE HTML PUBLIC \"-//W3C//DTD HTML 4.01 Transitional//EN\"\n        \"http://www.w3.org/TR/1999/REC-html401-19991224/loose.dtd\">\n<html lang=\"{language}\">\n<!-- $Id: templates.sql,v 1.37 2003/07/06 12:50:43 glen Exp $ -->\n<head>\n<meta http-equiv=\"content-type\" content=\"text/html; charset={charset}\"/>\n<meta name=\"generator\" content=\"Siteframe {version}\"/>\n<title>{page_title}</title>\n<link type=\"text/css\" rel=\"stylesheet\"\n    href=\"{site_path}/themes/{theme}/print.css\"\n    media=\"print\">\n<link type=\"text/css\" rel=\"stylesheet\"\n    href=\"{site_path}/themes/{theme}/screen.css\"\n    media=\"screen\">\n{head_content}\n{rdf}\n</head>\n\n<body>\n<table cellpadding=\"0\" cellspacing=\"0\" border=\"0\" width=\"100%\">\n<tr><td colspan=\"2\" id=\"title\">\n    {banner}\n    {!if defined(logo)\n        \'<a href=\"{site_path}/\"><img src=\"{logo}\" alt=\"{site_name}\" border=\"0\"/></a>\'\n        \'<h1>{site_name}</h1>\'\n    !}\n    <div id=\"description\">{site_description}</div>\n    </td>\n<tr><td colspan=\"2\" id=\"menu\">\n        <table cellpadding=\"0\" cellspacing=\"0\" border=\"0\" width=\"100%\"><tr><td valign=\"bottom\">\n            <a href=\"{site_path}/\">Home</a> | <a href=\"{site_path}/login.php\">Login</a>\n            {!if defined(user_id)\n                \'| <a href=\"{site_path}/user.php\">My Page</a>\n                 | <a href=\"{site_path}/prefs.php\">Preferences</a>\'\n            !}\n        </td><td align=\"right\">\n            <form action=\"{site_path}/search.php\" method=\"post\" class=\"searchform\">\n                Search: <input type=\"text\" name=\"searchfor\" size=\"12\"/>\n                <input type=\"hidden\" name=\"submitted\" value=\"1\"/>\n            </form>\n        </td></tr></table>\n    </td></tr>\n<tr><td id=\"navigation\" width=\"160px\" rowspan=\"2\" valign=\"top\"