INSERT INTO templates VALUES (NULL,'2003-06-28 18:54:47','2003-06-28 18:54:47',0,0,'slideshow','','<?xml version=\"1.0\" encoding=\"{charset}\"?>\n<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\"\n        \"http://www.w3.org/TR/2000/REC-xhtml1-20000126/DTD/xhtml1-transitional.dtd\">\n<html xmlns=\"http://www.w3.org/1999/xhtml\" xml:lang=\"{language}\" lang=\"{language}\">\n<head>\n<title>{folder_name}</title>\n<style type=\"text/css\">\n<!--\n    body {\n    background: black;\n    color: white;\n    margin: 2px;\n    }\n    p { margin: 0 0 2px 0; }\n    .heading {\n    font-family: arial,helvetica,sans-serif;\n    background: white;\n    color: black;\n    padding: 3px;\n    }\n    a { color: gray; text-decoration: none; }\n    .close {\n    font-size: 10px;\n    font-weight: bold;\n    float: right;\n    }\n    #title {\n    font-size: 18px;\n    font-weight: bold;\n    }\n    #author {\n    font-size: 12px;\n    color: gray;\n    }\n    table {\n    text-align:center;\n    }\n    td {\n    height:610px;\n    width:610px;\n    text-align:center;\n    vertical-align:middle;\n    background: black;\n    padding: 5px;\n    }\n-->\n</style>\n<script>\n// (C) 2000 www.CodeLifter.com\n// http://www.codelifter.com\n// Free for all users, but leave in this  header\n// NS4-6,IE4-6\n// Fade effect only in IE; degrades gracefully\n\n// =======================================\n// set the following variables\n// =======================================\n\n// Set slideShowSpeed (milliseconds)\nvar slideShowSpeed = 5000\n\n// Duration of crossfade (seconds)\nvar crossFadeDuration = 3\n\n// Specify the image files\nvar Pic = new Array() // do not touch this\nvar Title = new Array()\nvar Author = new Array()\n// to add more images, just continue\n// the pattern, adding to the array below\n\n{BEGIN:folder_docs folder_id fd}\nPic[{row_number}] = \"{doc_file_600}\"\nTitle[{row_number}] = \"{doc_title:noquote}\"\nAuthor[{row_number}] = \"{doc_user_name}\"\n{END:folder_docs}\n\n// =======================================\n// do not edit anything below this line\n// =======================================\n\nvar t\nvar j = 1\nvar p = Pic.length\n\nvar preLoad = new Array()\nfor (i = 1; i <= p; i++){\n   preLoad[i] = new Image()\n   preLoad[i].src = Pic[i]\n}\nvar title = document.all.item(\"title\")\nvar author = document.all.item(\"author\")\n\nfunction runSlideShow(){\n   if (document.all){\n      document.images.SlideShow.style.filter=\"blendTrans(duration=2)\"\n      document.images.SlideShow.style.filter=\"blendTrans(duration=crossFadeDuration)\"\n      document.images.SlideShow.filters.blendTrans.Apply()\n   }\n   document.images.SlideShow.src = preLoad[j].src\n   document.title = \"{folder_name} - \"+Title[j]\n   document.getElementById(\"title\").innerHTML=Title[j]\n   document.getElementById(\"author\").innerHTML=Author[j]\n   if (document.all){\n      document.images.SlideShow.filters.blendTrans.Play()\n   }\n   j = j + 1\n   if (j > (p-1)) j=1\n   t = setTimeout(\"runSlideShow()\", slideShowSpeed)\n}\n</script>\n</head>\n<body onload=\"runSlideShow()\">\n<div class=\"heading\">\n<p class=\"close\">[<a href=\"#\" onClick=\"javascript:self.close();\">Close</a>]</p>\n<p id=\"title\">Slideshow</p>\n<p id=\"author\">Please wait while images load...</p>\n</div>\n<table><tr><td>\n<img src=\"{site_path}/images/film.gif\" name=\'SlideShow\' alt=\"slideshow image\"/>\n</td></tr></table>\n</body>\n</html>');
