INSERT INTO templates VALUES (NULL,'2003-06-28 18:54:47','2003-06-28 18:54:47',0,0,'subscriptions','','<p>Your currently-active subscriptions are displayed below.\nFrom this page, you can choose to edit or remove any of your existing\nsubscriptions.</p>\n<ul>\n<li>To create a new subscription, navigate to the\ndocument, folder, or group you wish to subscribe to, then click\non the \"Subscribe\" link.</li>\n<li>To modify a subscription, click on the <b>Title</b>.</li>\n<li>To delete one or more subscriptions, check the box in the <b>Del</b> column,\nthen press <b>Delete Checked Subscriptions</b>.</li>\n</ul>\n\n<form method=\"post\" action=\"{site_path}/subscriptions.php?id={subscr_user_id}\" enctype=\"multipart/form-data\">\n<table class=\"list\">\n<tr>\n  <th>Type</th>\n  <th>Title</th>\n  <th>Add</th>\n  <th>Mod</th>\n  <th>Notification</th>\n  <th>Frequency</th>\n  <th>Del</th>\n</tr>\n{BEGIN:all_subscriptions subscr_user_id}\n<tr class=\"{row_class}\">\n  <td>{subscr_obj_type_display}</td>\n  <td><a href=\"{site_path}/subscribe.php?id={subscr_id}\">{subscr_obj_title}</a></td>\n  <td align=\"center\">{!if \'{subscr_notify_add}\' \'Y\' \'N\'!}</td>\n  <td align=\"center\">{!if \'{subscr_notify_mod}\' \'Y\' \'N\'!}</td>\n  <td>{subscr_notify_type_display}</td>\n  <td>{subscr_notify_frequency_display}</td>\n  <td align=\"center\">\n    <input type=\"checkbox\" name=\"remove[]\" value=\"{subscr_id}\"/>\n  </td>\n</tr>\n{END:all_subscriptions}\n</table>\n<p class=\"doc\">Click the <b>Del</b> checkbox and press <b>Delete Checked\nSubscriptions</b> to remove one or more subscriptions. Deletion is immediate;\nthere is no confirmation.</p>\n<input type=\"hidden\" name=\"submitted\" value=\"1\"/>\n<input type=\"submit\" value=\"Delete Checked Subscriptions\"/>\n<input type=\"reset\"/>\n</form>\n\n{!menu_item subscr_action \'documents\'\n  \'{site_path}/subscribe.php?type=D\' \'{subscr_all_objects}\'!}\n{!menu_item subscr_action \'folders\'\n  \'{site_path}/subscribe.php?type=F\' \'{subscr_all_objects}\'!}\n{!menu_item subscr_action \'groups\'\n  \'{site_path}/subscribe.php?type=G\' \'{subscr_all_objects}\'!}\n{!menu_item subscr_action \'users\'\n  \'{site_path}/subscribe.php?type=U\' \'{subscr_all_objects}\'!}\n<p class=\"action\">{!if \'{subscr_all_objects}\' \'Subscribe to all:\'!}\n{!menu subscr_action \' {sep} \'!}\n</p>');
INSERT INTO templates VALUES (NULL,'2003-06-22 22:22:35','2003-06-22 22:22:35',13,0,'page','./themes/Siteframe/page.ihtml','<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Strict//EN\"\n\"http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd\">\n<!-- $Id: templates.sql,v 1.37 2003/07/06 12:50:43 glen Exp $ -->\n<html xmlns=\"http://www.w3.org/1999/xhtml\" xml:lang=\"{language}\" lang=\"{language}\">\n<head>\n<title>{page_title}</title>\n<meta http-equiv=\"content-type\" content=\"text/html; charset={charset}\"/>\n<meta name=\"generator\" content=\"Siteframe {siteframe_version}\"/>\n{!if defined(site_meta_keywords)\n\'<meta name=\"keywords\" content=\"{site_meta_keywords}\"/>\'\n!}\n<link rel=\"stylesheet\" type=\"text/css\" media=\"print\"\n      href=\"{site_path}/themes/{theme}/printer.css\"/>\n<link rel=\"stylesheet\" type=\"text/css\" media=\"screen\"\n      href=\"{site_path}/themes/{theme}/siteframe.css\"/>\n{!if defined(head_content) \'{head_content}\'!}\n{rdf}\n</head>\n<body>\n<table width=\"100%\" cellspacing=\"0\" cellpadding=\"0\" border=\"0\" id=\"page\">\n<tr>\n  <td colspan=\"3\" id=\"title\">\n      {!if defined(logo)\n        \'<a href=\"{site_path}/\"><img src=\"{logo}\" alt=\"{site_name}\" class=\"logo\"/></a>\'\n        \'<a href=\"{site_path}/\">{site_name}</a>\'\n      !}\n  </td>\n</tr>\n<tr>\n  <td colspan=\"3\" id=\"topmenu\">\n    {!if defined(user_id)\n    \' {user_name} | <a href=\"{site_path}/user.php\">My Page</a>\n      | <a href=\"{site_path}/prefs.php\">Prefs</a>\n      | <a href=\"{site_path}/login.php\">Logout</a>\'\n    \' <a href=\"{site_path}/register.php\">Register</a>\n      | <a href=\"{site_path}/login.php\">Login</a>\'\n    !}\n  </td>\n</tr>\n<tr>\n  <td id=\"left\">\n  {navigation}\n  </td>\n  <td id=\"center\">\n    <p class=\"folderpath\">\n    {!if defined(folder_path)\n    \'{folder_path}\'\n    \'<a href=\"{site_path}/\">Home</a>\'!}&nbsp;</p>\n    {!if defined(no_title) \'\' \'<h1>{page_title}</h1>\'!}\n    {!if defined(e