INSERT INTO templates VALUES (NULL,'2003-06-22 22:22:35','2003-06-22 22:22:35',13,0,'popup','./themes/Siteframe/popup.ihtml','<!-- $Id: templates.sql,v 1.37 2003/07/06 12:50:43 glen Exp $ -->\n<html>\n<head>\n<title>{page_title}</title>\n<style type=\"text/css\">\nbody, th, td {\nfont-family:verdana,arial,helvetica,sans-serif;\nfont-size: 11px;\n}\n.prompt,.field,.doc,p {\nmargin: 0;\ndisplay: block;\n}\n.prompt {\nfont-weight: bold;\n}\n.doc {\nmargin-bottom: 5px;\ncolor: gray;\nfont-size: 10px;\n}\n.button {\nfont-size: 9px;\n}\ntextarea {\nwidth: 350px;\n}\nform {\nmargin: 5px 0 0 0;\n}\nh4 {\nfont-family: arial,helvetica,sans-serif;\nmargin: 0;\nbackground: tan;\npadding: 2px;\n}\n.error {\ncolor: red;\nfont-style: italic;\nmargin: 10px 0 10px 0;\n}\n</style>\n</head>\n<body>\n<h4>{page_title}</h4>\n{!if defined(error) \'<p class=\"error\">{error}</p>\'!}\n{body}\n<p class=\"button\">\n  <small>[<a href=\"#\" onClick=\"javascript:self.close();\">Close</a>]</small>\n</p>\n</body>\n</html>\n');
