INSERT INTO templates VALUES (NULL,'2003-06-22 22:22:35','2003-06-22 22:22:35',13,0,'siteframe.css','./themes/Siteframe/siteframe.css','/* $Id: templates.sql,v 1.37 2003/07/06 12:50:43 glen Exp $\n** copyright (c)2002, broadpool, llc. all rights reserved.\n** this cascading stylesheet contains screen formatting materials\n** for the \"Siteframe\" theme\n*/\nbody {\nbackground: white;\ncolor: black;\nmargin: 0;\npadding: 0;\nfont-family: verdana,arial,helvetica,sans-serif;\nfont-size: 12px;\n}\n\n/* default hyperlinks */\na {\ncolor: mediumslateblue;\ntext-decoration: none;\n}\na:hover {\ncolor: dodgerblue;\n}\n\n/* the #page table encompasses the entire viewport */\n#page {\nmargin: 0;\n}\n\n/* the #title division runs the full width of the viewport\n** at the very top of the screen */\n#title {\nbackground: white;\nfont-family: georgia,times new roman,times,serif;\nfont-weight: bold;\nvertical-align: bottom;\nfont-size: 30px;\npadding: 0 5px 0 5px;\ncolor: midnightblue;\n}\n#title a,#title a:visited {\ncolor: midnightblue;\ntext-decoration: none;\n}\n#title a:hover {\ncolor: darkslateblue;\ntext-decoration: none;\n}\n/* this little menu immediately follows the #title and acts as a\n** separator for the remainder of the page */\n#topmenu {\nfont-size: 10px;\nfont-weight: bold;\nbackground: black;\ncolor: silver;\npadding: 2px;\nborder-top: 1px gray solid;\nborder-bottom: 1px gray solid;\ntext-align: right;\n}\n#topmenu a {\ncolor: steelblue;\ntext-decoration: none;\n}\n#topmenu a:hover {\ncolor: dodgerblue;\n}\n\n/* the #main division covers the entire rest of the viewing space */\n#main {\n}\n#left,#right {\nwidth: 130px;\nfont-size: 11px;\nbackground: silver;\n}\n\n/* the code for this left-hand menu\n** was adapted from http://alistapart.com/stories/taminglists/\n** by Mark Newhouse\n*/\n#left {\npadding: 0;\nwhite-space: nowrap;\n}\n#left ul {\nlist-style: none;\nmargin: 0;\npadding: 0;\nborder: none;\n}\n#left a {\nborder-bottom: 1px black solid;\nmargin: 0;\nfont-weight: bold;\n}\n#left .title {\npadding: 2px 2px 2px 7px;\nborder-bottom: 1px black solid;\nmargin: 0;\nfont-weight: bold;\nbackground: gray;\ncolor: white;\n}\n#left .info {\npadding: 2px 2px 2px 7px;\nfont-weight: normal;\n}\n#left a {\ndisplay: block;\nwidth: 100%;\ntext-decoration: none;\nbackground: silver;\ncolor: navy;\npadding: 2px;\nborder-left: 5px gray solid;\n}\nhtml>body #left a {\nwidth: auto;\n}\n#left a:hover {\nbackground-color: white;\ncolor: darkslategray;\nborder-left: 5px red solid;\n}\n#left br {\ndisplay: none;\n}\n\n/* the right-hand column is a rather arbitrary one; it contains\n** \"extra stuff\" to be defined in context. For example, on document\n** pages it contains a list of other documents in the same folder.\n*/\n#right a {\ncolor: navy;\ntext-decoration: none;\n}\n#right a:hover {\ncolor: slateblue;\n}\n#right {\nline-height: 150%;\nfont-size: 11px;\npadding: 5px;\n}\n#right p {\nmargin: 0 0 5px 0;\n}\n#center {\nbackground: white;\ncolor: black;\npadding: 10px;\nmargin: 0 145px 0 132px;\nborder: 1px gray solid;\nborder-top: none;\n}\n\n/* the content area is where the primary text goes */\n#content {\nline-height: 150%;\n}\n#content a {\ncolor: navy;\ntext-decoration: none;\n}\n#content a:hover {\ncolor: slateblue;\ntext-decoration: underline;\n}\n#content a:active {\ncolor: crimson;\ntext-decoration: none;\n}\n\n/* the footer goes at the bottom */\n#footer {\ncolor: gray;\nfont-size: 10px;\nmargin-top: 20px;\n}\n#footer a {\ncolor: darkslategray;\ntext-decoration: none;\n}\n#footer a:hover {\ntext-decoration: underline;\n}\n\nh1 {\nfont-family: arial,helvetica,sans-serif;\nfont-weight: normal;\ncolor: gray;\n}\nh2,h3,h4 {\nfont-family: arial,helvetica,sans-serif;\nfont-weight: normal;\ncolor: gray;\nmargin-top: 20px;\nmargin-bottom: 5px;\n}\n\nimg.logo {\nborder: none;\npadding: 2px;\n}\n\np {\nmargin-top: 0;\npadding-top: 0;\n}\np.action {\nclear:both;\ntext-align: left;\n}\np.action a {\ncolor: gray;\n}\np.ahem {\nline-height: 200%;\ncolor: red;\ndisplay: none;\n}\np.error {\nfont-family: verdana,arial,helvetica,sans-serif;\ncolor: crimson;\nmargin: 10px 0 10px 0;\nline-height: 150%;\n}\np.info {\nmargin: 5px 0 5px 0;\ncolor: gray;\nfont-size: 10px;\n}\ndiv.path {\ndisplay: none;\n}\np.folderpath {\nfont-family: verdana,arial,helvetica,sans-serif;\nfont-size: 10px;\ncolor: gray;\nmargin: 0;\npadding: 0;\n}\np.folderpath a {\ncolor: gray;\ntext-decoration: none;\n}\np.folderpath a:hover {\ncolor: dodgerblue;\n}\np.recordset {\nfont-size: 10px;\ncolor: silver;\n}\np.recordset a {\ncolor: gray;\ntext-decoration: none;\n}\np.recordset a:visited {\ncolor: lightgray;\n}\np.recordset a:hover {\ncolor: black;\ntext-decoration: underline;\n}\n\n/* comments */\np.subject {\nbackground: silver;\nfont-weight: bold;\nfont-size: 11px;\npadding: 1px;\nmargin: 5px 0 2px 0;\ncolor: black;\n}\n.comment p {\nmargin: 0;\npadding: 0;\n}\n\n/* page editing */\n#content div.edit {\nposition: absolute;\ntop: 0;\nright: 0;\npadding: 2px;\nmargin: 5px;\nbackground: snow;\nborder: 1px gray solid;\ncolor: gray;\nfont-size: 10px;\nfont-weight: bold;\n}\n#content p.editor {\nmargin: 0;\npadding: 0;\n}\n\nform {\nfont-family: verdana,arial,helvetica,sans-serif;\nfont-size: 11px;\n}\nform hr {\ndisplay: none;\n}\nform p {\nmargin: 0;\npadding: 2px;\n}\nform p.prompt {\nmargin-top: 5px;\nfont-weight: bold;\ncolor: gray;\n}\nform p.field {\n}\nform p.doc {\ncolor: gray;\nborder-bottom: 1px dotted silver;\nmargin-bottom: 5px;\n}\ntextarea {\nfont-family: verdana,arial,helvetica,sans-serif;\nwidth: 600px;\n}\n\n/* divisions */\ndiv.imageboxes {\ndisplay: block;\n}\n\n/* tables */\ntable {\nmargin: 20px 0 20px 0;\npadding: 0;\n}\ntr.even {\nbackground: #eeeeee;\npadding: 1px;\n}\nth {\nfont-size: 11px;\nfont-weight: bold;\ntext-align: left;\nborder-bottom: 1px silver solid;\n}\n.infotable th {\nborder: none;\nvertical-align: top;\n}\ntd {\nvertical-align: top;\nfont-size: 11px;\n}\ntd.event_date {\nwhite-space: nowrap;\n}\ntable.list {\nclear: both;\nborder-bottom: 1px silver solid;\n}\ntable.list td {\npadding: 3px;\nline-height: 130%;\n}\n.listinfo {\nwhite-space:nowrap;\n}\n.nowrap {\nwhite-space:nowrap;\n}\n\n/* definition lists */\ndt {\nfont-weight: bold;\n}\n\n/* \"box\" display for front page */\ntable.box {\nwidth: 100%;\nmargin: 0;\n}\ntd.box {\npadding: 10px;\nwidth: 33%;\nheight: 200px;\nborder: 1px gray dotted;\nfont-size: 11px;\n}\ntd.box p {\nmargin-bottom: 5px;\npadding: 0;\n}\ntd.box h2, td.box h3, td.box h4, .infobox h2, .infobox h3, .infobox h4 {\nmargin-top: 0;\nfont-weight: bold;\n}\n\ndiv.infobox {\nmargin-top: 10px;\nmargin-bottom: 10px;\npadding: 10px;\nborder: 1px silver solid;\n}\n\n.q {\nfont-family: arial,helvetica,sans-serif;\nfont-weight: bold;\nfont-size: 14px;\ncolor: saddlebrown;\n}\n\n/* folders */\ndiv.subfolders {\n/*display: none;*/\n}\ndiv.subfolder {\nfont-family: verdana,arial,helvetica,sans-serif;\nfont-size: 10px;\nline-height: 100%;\nwidth: 90px;\nheight: 90px;\noverflow: auto;\nborder: 1px gray solid;\npadding: 2px;\nmargin: 2px;\ntext-align: center;\nfloat: left;\n}\n\nhr {\nheight: 1px;\ncolor: silver;\nmargin: 0;\npadding: 0;\n}\n\n.portal {\nwidth: 100%;\n}\n.portal td {\nmargin: 5px;\npadding: 0;\nborder: 1px navy solid;\n}\n.portal td h3 {\nmargin: 0;\npadding: 5px;\nbackground: navy;\ncolor: white;\nfont-size: 14px;\nfont-weight: bold;\n}\n.portal td p {\npadding: 5px;\n}\na img {\nborder: none;\n}\n\ndiv.notices {\nmargin-left: 1em;\ntext-indent: -1em;\n}\ndiv.notices p {\ndisplay: inline;\n}\n\n.messages {\ntext-align: center;\nmargin: 5px;\npadding: 5px;\nborder: 1px yellow solid;\n}\n#left .messages a {\nborder: none;\ndisplay: inline;\nfont-weight: bold;\n}\n#left .messages br {\ndisplay: inline;\n}');
INSERT INTO templates VALUES (NULL,'2003-06-28 18:54:47','2003-06-28 18:54:47',0,0,'results','','<table cellpadding=\"0\" cellspacing=\"2px\" class=\"inputform\">\n{BEGIN:poll_results}\n<tr><td>{poll_option}</td>\n    <td align=\"right\">&nbsp;{poll_votes}</td>\n    <td align=\"right\">&nbsp;{poll_percent}%</td>\n    <td>&nbsp;<span class=\"pollbar\">{poll_bar}</span></td>\n</tr>\n{END:poll_results}\n</table>\n<p>{poll_total} votes have been cast<br>\n<a href=\"{site_path}/document.php?id={doc_id}&revote=1\">Click here to change your vote</a>\n</p>');
INSERT INTO templates VALUES (NULL,'2003-06-28 18:54:47','2003-06-28 18:54:47',0,0,'rss','','<?xml version=\"1.0\" encoding=\"iso-8859-1\" ?>\n<!-- name=\"generator\" content=\"siteframe/{siteframe_version}\" -->\n<rss version=\"0.92\">\n  <channel>\n    <title>{site_name}</title>\n    <link>{site_url}</link>\n    <copyright>Copyright {!date \'Y\'!}, {site_name}. All Rights Reserved.</copyright>\n    <managingEditor>{site_email}</managingEditor>\n    <lastBuildDate>{!date \'F j, Y h:ia T\'!}</lastBuildDate>\n    <docs>http://backend.userland.com/rss092</docs>\n  {!if defined(site_description)\n    \'<description>{site_description:nohtml}</description>\'\n  !}\n  {!if defined(logo)\n    \'<image>\n        <url>{site_url}/{logo}</url>\n        <title>{site_name:nohtml}</title>\n        <link>{site_url}</link>\n     </image>\'\n  !}\n  {BEGIN:recent_docs num_rss _rdx}\n    <item>\n        <title>{doc_title:nohtml}</title>\n        <link>{site_url}/document.php?id={doc_id}</link>\n        <description>\n        {!if defined(doc_summary)\n        \'{doc_summary:nohtml}\'\n        \'{doc_body:nohtml}\'\n        !}\n        </description>\n        {!if \'(\"{doc_type}\"==\"Image\")||(\"{doc_type}\"==\"File\")\'\n        \'<enclosure url=\"{site_url}/{doc_file_100}\" type=\"{doc_file_mime_type}\"/>\'\n        !}\n    </item>\n  {END:recent_docs}\n  </channel>\n</rss>');
INSERT INTO templates VALUES (NULL,'2003-06-26 19:31:39','2003-06-26 19:31:39',14,0,'form','themes/Swiss/form.ihtml','<!-- $Id: templates.sql,v 1.37 2003/07/06 12:50:43 glen Exp $ -->\n<p>{form_instructions}</p>\n\n{!menu_item form_action \'{folder_name}\'\n  \'{site_path}/folder.php?id={folder_id}\'\n  \'{folder_id}+0\'!}\n{!menu_item form_action \'{doc_folder_name}\'\n  \'{site_path}/folder.php?id={doc_folder_id}\'\n  \'{doc_folder_id}+0\'!}\n{!menu_item form_action \'{doc_title}\'\n  \'{site_path}/document.php?id={doc_id}\'\n  \'{doc_id}+0\'!}\n<p class=\"action\">\n{!menu form_action \' {sep} \'!}\n</p>\n\n<form method=\"post\" action=\"{form_action}\" name=\"{form_name}\" enctype=\"multipart/form-data\">\n{BEGIN:input_form}\n{!if \'\"{input_form_type}\"==\"ignore\"\'\n  \'{input_form_prompt}\'\n  \'<p class=\"prompt\">{input_form_prompt}:</p>\'\n!}\n{!if defined(input_form_doc) \'<p class=\"doc\">{input_form_doc}</p>\'!}\n<p class=\"field\">{input_form_field}</p>\n{END:input_form}\n<input type=\"hidden\" name=\"submitted\" value=\"1\"/>\n{BEGIN:input_form_hidden}\n{hidden_form_field}\n{END:input_form_hidden}\n<input type=\"submit\" value=\"Submit\"/>\n<input type=\"reset\"/>\n</form>');
INSERT INTO templates VALUES (NULL,'2003-06-26 19:31:39','2003-06-26 19:31:39',14,0,'page','themes/Swiss/page.ihtml','<?xml version=\"1.0\" encoding=\"{charset}\"?>\n<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\"\n        \"http://www.w3.org/TR/2000/REC-xhtml1-20000126/DTD/xhtml1-transitional.dtd\">\n<html xmlns=\"http://www.w3.org/1999/xhtml\" xml:lang=\"{language}\" lang=\"{language}\">\n<head>\n<title>{page_title:STRIPTAGS}</title>\n<link rel=\"stylesheet\" type=\"text/css\" href=\"{site_path}/themes/{theme}/swiss.css\"/>\n{head_content}\n{rdf}\n</head>\n<body>\n\n{!menu_item topmenu \'Login\' \'{site_path}/login.php\' \'!({user_id}+0)\'!}\n{!menu_item topmenu \'Register\' \'{site_path}/register.php\' \'!({user_id}+0)\'!}\n{!menu_item topmenu \'Logout\' \'{site_path}/login.php\' \'{user_id}+0\'!}\n{!menu_item topmenu \'My Page\' \'{site_path}/user.php?id={user_id}\' \'{user_id}+0\'!}\n{!menu_item topmenu \'Prefs\' \'{site_path}/prefs.php\' \'{user_id}+0\'!}\n\n<table id=\"top\" width=\"96%\" align=\"center\">\n<tr>\n{!if defined(logo) \'<td rowspan=\"2\" id=\"logo\"><a href=\"{site_url}\" title=\"{site_name}\"><img src=\"{logo}\" alt=\"{site_name} logo\"/></a></td>\'!}\n<td width=\"99%\">\n<p id=\"topmenu\">{!menu topmenu \' &nbsp; &nbsp; \'!}</p>\n<h1><a href=\"{site_url}\">{site_name}</a></h1></td></tr>\n<tr><td class=\"sitedesc\">\n{!if defined(folder_path) \'{folder_path}\' \'{site_description}\'!}\n</td></tr>\n</table>\n\n<table id=\"content\">\n<tr>\n{!if defined(no_navigation) \n  \'\'\n  \'<td id=\"left\">\n   <div class=\"menu\">\n     <p class=\"title\">Menu</p>\n     {navigation}\n   </div></td>\n\'!}\n<td id=\"right\">\n{!if defined(no_title) \'\' \'<h1>{page_title}</h1>\'!}\n{!if defined(error) \'<p class=\"error\">{error}</p>\'!}\n{recordset}\n{body}\n</td></tr></table>\n\n<table id=\"footer\" width=\"96%\" align=\"center\">\n<tr><td>{footer}</td></tr>\n</table>\n\n</body>\n</html>');
INSERT INTO templates VALUES (NULL,'2003-06-26 19:31:39','2003-06-26 20:03:37',14,0,'popup','themes/Swiss/popup.ihtml','<?xml version=\"1.0\" encoding=\"{charset}\"?>\r\n<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\"\r\n        \"http://www.w3.org/TR/2000/REC-xhtml1-20000126/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\" xml:lang=\"{language}\" lang=\"{language}\">\r\n<head>\r\n<title>Control Panel - {page_title:STRIPTAGS}</title>\r\n<link rel=\"stylesheet\" type=\"text/css\" href=\"{site_path}/themes/{theme}/swiss.css\"/>\r\n<style type=\"text/css\">\r\n<!--\r\nbody {\r\nbackground: ghostwhite;\r\nmargin: 0;\r\npadding: 10px;\r\nfont-family: arial,helvetica,verdana,sans-serif;\r\n}\r\ntextarea {\r\nfont-family: arial,helvetica,verdana,sans-serif;\r\nwidth: auto;\r\n}\r\nform {\r\nborder: none;\r\npadding: 0;\r\nwidth: auto;\r\n}\r\n#top {\r\nborder-bottom: 1px darkgreen solid;\r\nfont-weight: bold;\r\n}\r\n.action {\r\ndisplay: none;\r\n}\r\n-->\r\n</style>\r\n{head_content}\r\n</head>\r\n<body>\r\n<div id=\"top\">\r\n{site_name}\r\n</div>\r\n\r\n<h3>{page_title}</h3>\r\n{!if defined(error) \'<p class=\"error\">{error}</p>\'!}\r\n{recordset}\r\n{body}\r\n\r\n<div id=\"footer\">\r\n{footer}\r\n</div>\r\n\r\n</body>\r\n</html>');
INSERT INTO templates VALUES (NULL,'2003-06-26 19:31:39','2003-06-29 18:41:48',14,0,'swiss.css','themes/Swiss/swiss.css','body {\r\nbackground: white;\r\ncolor: black;\r\n}\r\nbody, td, th {\r\nfont-family: arial,helvetica,sans-serif;\r\nfont-size: 12px;\r\n}\r\n#top {\r\nmargin-bottom: 20px;\r\n}\r\n#top h1 {\r\nborder-bottom: 1px darkgreen solid;\r\nmargin: 0;\r\nbackground: white;\r\nletter-spacing: 0;\r\nfont-size: 18px;\r\n}\r\n#top h1 a, #top h1 a:visited, #top h1 a:active, #top h1 a:hover {\r\ncolor: black;\r\ntext-decoration: none;\r\n}\r\n#logo {\r\nvertical-align: top;\r\n}\r\n#topmenu {\r\nfloat: right;\r\nmargin: 0 5px 0 0;\r\n}\r\n.sitedesc {\r\nfont-style: italic;\r\nvertical-align: top;\r\n}\r\n#content {\r\nwidth: 92%;\r\nmargin: 0 4% 0 4%;\r\nline-height: 125%;\r\n}\r\n#footer {\r\nwidth: 96%;\r\nmargin-top: 20px;\r\nborder-top: 1px darkgreen solid;\r\ncolor: gray;\r\ntext-align: center;\r\n}\r\n#footer td {\r\ncolor: gray;\r\ntext-align: center;\r\n}\r\n#left {\r\nwidth: 20%;\r\npadding-right: 10px;\r\n}\r\n#right {\r\nwidth: 80%;\r\nfont-family: times new roman, times, serif;\r\nfont-size: small;\r\n}\r\n#right p {\r\nmargin-top: 0;\r\n}\r\n.menu {\r\nbackground: #ccffcc;\r\nborder: 2px darkgreen solid;\r\npadding: 15px;\r\n}\r\n.menu b {\r\nmargin-top: 3px;\r\n}\r\n.menu p {\r\nmargin: 0 0 10px 0;\r\ncolor: darkgreen;\r\n}\r\n.menu .title {\r\nfont-weight: bold;\r\ncolor: darkgreen;\r\ntext-align: center;\r\n}\r\n\r\n.popup {\r\n}\r\n.popup form, .popup textarea {\r\nwidth: auto;\r\n}\r\n\r\n#weblog, .Document, .Comments {\r\nwidth: 600px;\r\nmargin-bottom: 15px;\r\n}\r\n\r\n/* hyperlinks */\r\na {\r\ncolor: mediumblue;\r\ntext-decoration: underline;\r\n}\r\na:active {\r\ncolor: red;\r\n}\r\na:visited {\r\ncolor: purple;\r\n}\r\n\r\n/* headings */\r\nh1,h2,h3,h4 {\r\nfont-family: arial,helvetica,sans-serif;\r\nline-height: 120%;\r\nmargin-bottom: 5px;\r\n}\r\nh1 {\r\nfont-size: 24px;\r\nmargin: 0 0 30px 0;\r\nletter-spacing: -1px;\r\n}\r\nh2 { font-size: 18px; }\r\nh3 { font-size: 15px; }\r\nh4 { font-size: 12px; }\r\nh2.date {\r\ncolor: darkgreen;\r\nfont-size: 12px;\r\nletter-spacing: 2px;\r\nborder-bottom: 1px gray solid;\r\n}\r\nh3.title {\r\nfont-size: 18px;\r\n}\r\n\r\n/* tables */\r\ntd {\r\nvertical-align: top;\r\n}\r\nth {\r\nfont-weight: bold;\r\nbackground: ghostwhite;\r\nborder-bottom: 1px gray solid;\r\n}\r\n.infotable {\r\nwidth: 600px;\r\nborder: 2px darkgreen solid;\r\n}\r\n.infotable th, .intotable td {\r\nbackground: silver;\r\n}\r\n.infotable td, .infotable th, .list th, .list td, .inputform td {\r\n}\r\n.infotable th {\r\ntext-align: right;\r\nvertical-align: top;\r\nwidth: 150px;\r\nborder-right: 1px gray solid;\r\n}\r\n.list {\r\nborder-top: 1px gray solid;\r\nborder-bottom: 1px gray solid;\r\npadding: 2px 10px 2px 10px;\r\nmargin: 10px 0 10px 0;\r\n}\r\n.list p {\r\nmargin: 0 0 3px 0;\r\n}\r\n\r\n/* images */\r\nimg { border: none; }\r\n\r\n/* input forms */\r\nform {\r\nline-height: 120%;\r\nwidth: 600px;\r\nborder-top: 1px gray solid;\r\nborder-bottom: 1px gray solid;\r\npadding: 10px 0 10px 0;\r\n}\r\n.prompt { \r\nfont-family: arial,helvetica,sans-serif;\r\nfont-size: 13px;\r\nmargin: 10px 0 2px 0; \r\nfont-weight: bold; \r\n}\r\n.doc { \r\nfont-family: arial,helvetica,sans-serif;\r\nfont-size: 12px;\r\nmargin: 2px 0 2px 0; \r\ncolor: darkgray; \r\n}\r\n.field { \r\nmargin: 2px 0 10px 0; \r\n}\r\ntextarea {\r\nwidth: 500px;\r\n}\r\n\r\n/* various paragraphs */\r\n.info, .author, .recordset {\r\nfont-family: arial,helvetica,sans-serif;\r\ncolor: gray;\r\nfont-size: 12px;\r\n}\r\n.action {\r\nfont-family: arial,helvetica,sans-serif;\r\npadding: 2px;\r\ntext-align: center;\r\nclear: both;\r\nfont-size: 12px;\r\n}\r\n.action a {\r\nmargin: 0 3px 0 3px;\r\n}\r\n.error {\r\nfont-family: arial,helvetica,sans-serif;\r\ncolor: red;\r\nclear: both;\r\nfont-size: 12px;\r\n}\r\n.Comments {\r\nfont-family: arial,helvetica,sans-serif;\r\nfont-size: 12px;\r\n}\r\n.Comments p {\r\nmargin-top: 0;\r\nmargin-bottom: 10px;\r\n}\r\n.Comments .subject {\r\nfont-weight: bold;\r\nfont-size: 13px;\r\nmargin-top: 10px;\r\nmargin-bottom: 0;\r\n}\r\n\r\n/* folders and image boxes */\r\n.subfolders,.imageboxes {\r\nmargin: 5px 0 5px 0;\r\nclear: both;\r\n}\r\n.subfolder,.picbox,.picboxlong {\r\ntext-align:center;\r\nfont-family: verdana,arial,helvetica,sans-serif;\r\nfont-size: 11px;\r\nline-height: 13px;\r\nmargin: 2px;\r\npadding: 2px;\r\nborder: 1px #eeeeee solid;\r\nfloat: left;\r\n}\r\n.subfolder {\r\nwidth: 90px;\r\nheight: 90px;\r\n}\r\n.picbox {\r\nwidth: 110px;\r\nheight: 110px;\r\n}\r\n.picboxlong {\r\nwidth: 110px;\r\nheight: 140px;\r\n}\r\n\r\n.backnext {\r\nfloat:right;\r\n}\r\n\r\n/* division */\r\n.image {\r\ntext-align: center;\r\npadding: 20px;\r\nbackground: ghostwhite;\r\nfont-family: arial,helvetica,sans-serif;\r\nfont-size: 12px;\r\nmargin-bottom: 15px;\r\n}\r\n.description {\r\ntext-align: left;\r\n}\r\n.path {\r\nfont-style: normal;\r\ncolor: gray;\r\n}\r\n\r\n\r\n/* miscellaneous */\r\n.pollbar {\r\nbackground: darkgreen;\r\n}\r\n.odd {\r\npadding: 1px;\r\n}\r\n.even {\r\nbackground: ghostwhite;\r\npadding: 1px;\r\n}\r\n.messages {\r\nbackground: yellow;\r\npadding: 10px;\r\ntext-align: center;\r\nline-height: 120%;\r\nmargin: 10px 0 10px 0;\r\n}');
INSERT INTO templates VALUES (NULL,'2003-06-28 18:54:47','2003-06-28 18:54:47',0,0,'ratings','','{!# $Id: templates.sql,v 1.37 2003/07/06 12:50:43 glen Exp $\n### Copyright (c)2001-2003, Broadpool, LLC. All rights reserved. See LICENSE.txt for details.\n### displays ratings\n#!}\n{!set page_title \'{doc_title:Ratings for \"%s\"}\'!}\n<p class=\"info\">\n<a href=\"{site_path}/document.php?id={doc_id}\">{!if \'\"{doc_type}\"==\"Image\"\'\n    \'<img src=\"{doc_file_150}\" border=\"0\" alt=\"\" title=\"{doc_title}\">\'\n    \'Return to {doc_type}\'\n!}\n</a></p>\n<p>Current rating: {doc_rating:%1.1f}/{doc_rating_count}</p>\n<h3>Distribution</h3>\n<table border=\"0\">\n<tr>\n    <th>Rating</th>\n    <th width=\"40px\">#</th>\n    <th width=\"40px\">%</th>\n    <th>Graph</th>\n</tr>\n{BEGIN:ratings}\n<tr>\n    <td align=\"right\">{rating}</td>\n    <td align=\"right\">{rating_count}</td>\n    <td align=\"right\">{rating_percent:%5.1f}%</td>\n    <td><span class=\"pollbar\">{rating_bar}</span></td>\n</tr>\n{END:ratings}\n</table>\n<h3>Ratings</h3>\n<table border=\"0\">\n<tr>\n    <th>User</th>\n    <th>Rating</th>\n</tr>\n{BEGIN:rating_users}\n<tr class=\"{row_class}\">\n    <td><a href=\"{site_path}/user.php?id={rating_user_id}\">{rating_user_name}</a></td>\n    <td align=\"right\">{user_rating}</td>\n</tr>\n{END:rating_users}\n</table>');
INSERT INTO templates VALUES (NULL,'2003-06-28 18:54:47','2003-06-28 18:54:47',0,0,'related_articles','','{!# related_articles.ihtml\n### displays other articles in the current folder\n### only works from /document.php\n### this will also not work on pages that include another document\n### iteration (such as a BEGIN:all_docs autoblock\n#!}\n<div class=\"sidebar\">\n<b>Related Articles</b><br/>\n{BEGIN:folder_docs doc_folder_id}\n{!if \'({main_doc_id}+0)==({doc_id}+0)\'\n  \'&gt; <b>{doc_title}</b>\'\n  \'&bull; <a href=\"/document.php?id={doc_id}\">{doc_title}</a>\'\n!}\n<br/>\n{END:folder_docs}\n</div>');
