INSERT INTO templates VALUES (NULL,'2003-06-28 18:54:47','2003-06-28 18:54:47',0,0,'pollfolder','','{!# $Id: templates.sql,v 1.37 2003/07/06 12:50:43 glen Exp $\n### Copyright (c)2001-2003, Broadpool, LLC. All rights reserved. See LICENSE.txt for details.\n### pollfolder - display a folder of polls\n#!}\n<p>{folder_body}</p>\n<h3>Contents</h3>\n<ul>\n{BEGIN:folder_docs folder_id x1}\n<li><a href=\"#poll{doc_id}\">{doc_title}</a></li>\n{END:folder_docs}\n</ul>\n\n<hr/>\n<p class=\"action\"><a href=\"{site_path}/edit.php?folder={folder_id}&class=Poll\">\nAdd Poll to folder</a></p>\n\n<table border=\"0\" width=\"100%\">\n{BEGIN:folder_docs folder_id x2}\n{!if \'\"{row_class}\"==\"odd\"\'\n    \'<tr>\'\n!}\n<td width=\"50%\">\n    <h3>{!bookmark \"poll{doc_id}\"!}{doc_title}</h3>\n    <p>{doc_body}</p>\n    {poll_display}\n    <p class=\"info\">by\n    <a href=\"{site_path}/user.php?id={doc_owner_id}\">{doc_user_name}</a>{!user_indicators doc!}\n    </p>\n</td>\n{!if \'\"{row_class}\"!=\"odd\"\'\n    \'</tr>\'\n!}\n{END:folder_docs}\n</table>\n<p class=\"info\">\n    folder owned by\n    <a href=\"{site_path}/user.php?id={folder_user_id}\">{folder_user_name}</a>{!user indicators folder!}\n</p>\n{!if editor(folder_owner_id)\n\'<div class=\"edit\"><p class=\"editor\">\n <a href=\"{site_path}/editfolder.php?id={folder_id}\">Edit</a>\n <a href=\"{site_path}/deletefolder.php?id={folder_id}\">Delete</a>\n </p></div>\'\n!}\n{template:editfolder}');
INSERT INTO templates VALUES (NULL,'2003-06-28 18:54:46','2003-06-28 18:54:46',0,0,'notify_html','','<div style=\"text-align:center; font-size:smaller; text-transform:uppercase; border-bottom: 1px black solid; margin-bottom: 40px;\">{site_name}</div>\n<h1>Subscription Notifications</h1>\n<p class=\"info\">{user_name}<br/>\n{!date \'{site_date_format} {site_time_format}\'!}</>\n\n<p><i>{notify_frequency_display} notifications</i></p>\n\n<hr/>\n\n<dl>\n{BEGIN:subscr_notifications user_id htmlversion}\n<dt>{!date \'{site_date_format} {site_time_format}\' \'{note_created}\'!}</dt>\n<dd><b>{note_message}</b><br/>\n<a href=\"{note_url}\">{note_url}</a>\n{!if defined(note_body) \'<blockquote><i>{note_body:nohtml}</i></blockquote>\'!}\n</dd>\n{END:subscr_notifications}\n</dl>\n\n<hr/>\n\n<p class=\"info\">\nThis message is sent from {site_name}<br/>\n<a href=\"{site_url}\">{site_url}</a><br/>\nYou can edit your subscriptions at\n<a href=\"{site_url}/subscriptions.php?id={user_id}\">{site_url}/subscriptions.php?id={user_id}</a>\n</p>');
