INSERT INTO templates VALUES (NULL,'2003-06-28 18:54:47','2003-06-28 18:54:47',0,0,'notify_online','','{!# notify - this template is used to generate popup subscription notifications\n## since the popup is intended to be a fairly small window, this template generates\n## the entire page, and not just the body. !}\n<?xml version=\"1.0\" encoding=\"{charset}\"?>\n<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\"\n        \"http://www.w3.org/TR/2000/REC-xhtml1-20000126/DTD/xhtml1-transitional.dtd\">\n<html xmlns=\"http://www.w3.org/1999/xhtml\" xml:lang=\"{language}\" lang=\"{language}\">\n<head>\n<title>{page_title}</title>\n<style type=\"text/css\">\nbody { font-family: verdana, arial, helvetica, sans-serif; font-size:12px; }\n#banner {\nfont-family: georgia, times new roman, times, serif;\nfont-size: 16px;\nfont-weight: bold;\ncolor: navy;\nborder-bottom: 1px gray solid;\nmargin-bottom: 5px;\npadding-bottom: 2px;\ntext-align:right;\n}\nh3 { color: gray; }\nblockquote { font-style:italic; margin:5px; }\n.item {\nborder-bottom: 1px gray solid;\npadding-bottom: 5px;\nmargin-bottom: 5px;\n}\n.info { text-align: right; font-size: smaller; }\n</style>\n{head_content}\n</head>\n<body>\n<div id=\"banner\">{site_name}</div>\n<h3>{page_title}</h3>\n{BEGIN:active_notifications user_id}\n<div class=\"item\">\n<p>{!date \'{site_date_format} {site_time_format}\' \'{note_created}\'!}<br/>\n<b>{note_message}</b><br/>\n<a href=\"#\" onClick=\"opener.location.href=\'{note_url}\';\">{note_url}</a></p>\n{!if defined(note_body) \'<blockquote>{note_body:nohtml}</blockquote>\'!}\n{!if defined(note_from_id)\n  \'<p class=\"info\">From <a href=\"{site_path}/user.php?id={note_from_id}\">{note_from}</a> [<a href=\"#\" onClick=\"window.open(&#34;{site_path}/message.php?id={note_from_id}&#34;,&#34;reply&#34;)\">Reply</a>]\'\n!}\n</div>\n{END:active_notifications}\n<form method=\"post\" action=\"\">\n<input type=\"hidden\" name=\"submitted\" value=\"1\"/>\n<input type=\"submit\" value=\"Acknowledge\"/>\n</form>\n</body>\n</html>');
