INSERT INTO templates VALUES (NULL,'2003-06-28 18:54:46','2003-06-28 18:54:46',0,0,'notices','','<h3>All Notices</h3>\n<table class=\"list\">\n<tr><th>Notice </th><th>Begin </th><th>End </th></tr>\n{BEGIN:all_notices}\n<tr class=\"{row_class}\">\n<td><div class=\"notices\">\n    <a href=\"{site_path}/document.php?id={doc_id}\"><b>{doc_title_display}</b></a>\n    {doc_body}</div></td>\n<td style=\"white-space:nowrap;\">\n  {!date \'{site_date_format}\' \'{notice_begin_date}\'!}</td>\n<td style=\"white-space:nowrap;\">\n  {!date \'{site_date_format}\' \'{notice_end_date}\'!}</td>\n</tr>\n{END:all_notices}\n</table>\n\n<h3>Active Notices</h3>\n<table class=\"list\">\n<tr><th>Notice </th><th>Begin </th><th>End </th></tr>\n{BEGIN:active_notices}\n<tr class=\"{row_class}\">\n<td><div class=\"notices\">\n    <a href=\"{site_path}/document.php?id={doc_id}\"><b>{doc_title_display}</b></a>\n    {doc_body}</div></td>\n<td style=\"white-space:nowrap;\">\n  {!date \'{site_date_format}\' \'{notice_begin_date}\'!}</td>\n<td style=\"white-space:nowrap;\">\n  {!date \'{site_date_format}\' \'{notice_end_date}\'!}</td>\n</tr>\n{END:active_notices}\n</table>\n\n<p class=\"action\">&bull;\n  <a href=\"{site_path}/edit.php?class=Notice\">Create a new Notice</a>\n</p>');
INSERT INTO templates VALUES (NULL,'2003-06-28 18:54:46','2003-06-28 18:54:46',0,0,'notification','','Notification of new {notify_type}\n{notify_title} {!if \'\"{notify_type}\"==\"document\"\' \'({doc_type_display})\'!}\n{site_url}/{notify_program}?id={notify_id}\n\nYou are receiving this message because you requested to be notified about any new {notify_type}s at {site_name}. If you no longer wish to receive these notifications, you can also your settings at {site_url}/prefs.php');
INSERT INTO templates VALUES (NULL,'2003-06-28 18:54:46','2003-06-28 18:54:46',0,0,'notifications','','<p>To delete a notification, click the checkbox beside it and\npress the <b>Delete Checked</b> button. Click on the \"Toggle All\" link to turn\ncheckboxes on and off.</p>\n\n<script language=\"JavaScript\" type=\"text/javascript\">\n<!--\nfunction CheckAll() {\n  for (var i = 0; i < document.noteform.elements.length; i++) {\n    if(document.noteform.elements[i].type == \'checkbox\'){\n      document.noteform.elements[i].checked = !(document.noteform.elements[i].checked);\n    }\n  }\n}\n//-->\n</script>\n\n<form method=\"post\" name=\"noteform\" action=\"{site_path}/notifications.php?id={user_user_id}\">\n<table class=\"list\">\n<tr>\n  <th>Created</th>\n  <th>Delivered</th>\n  <th>Message</th>\n  <th>Del</th>\n</tr>\n{BEGIN:all_notifications user_user_id}\n<tr class=\"{row_class}\">\n  <td>{!date \'{site_date_format} {site_time_format}\' \'{note_created}\'!}</td>\n  <td align=\"center\">{!if defined(note_sent) \'Yes\' \'-\'!}</td>\n  <td>\n<b>{note_message}</b>\n{!if defined(note_url)\n  \'<br/><a href=\"{note_url}\">{note_url}</a>\'!}\n{!if defined(note_body) \'<br/>{note_body:nohtml}\'!}\n{!if defined(note_from_id)\n  \'<br/><small>from <a href=\"{site_path}/user.php?id={note_from_id}\">{note_from}</a></small>\'\n!}\n  </td>\n  <td align=\"center\">\n    <input type=\"checkbox\" name=\"remove[]\" value=\"{note_id}\"/>\n  </td>\n</tr>\n{END:all_notifications}\n<tr><td colspan=\"4\" align=\"right\">\n  [<a href=\"#\" onClick=\"javascript:CheckAll();\">Toggle All</a>]\n</td></tr>\n</table>\n<input type=\"hidden\" name=\"submitted\" value=\"1\"/>\n<input type=\"submit\" value=\"Delete Checked\"/>\n<input type=\"reset\"/>\n</form>');
INSERT INTO templates VALUES (NULL,'2003-06-28 18:54:46','2003-06-28 18:54:46',0,0,'notify_ascii','','{!# template generates ASCII e-mail notifications !}\n{site_name} Subscription Notifications\n{user_name}\n{!date \'{site_date_format} {site_time_format}\'!}\n\n{notify_frequency_display} notifications\n\n-----------------------------------------------------------------------------\n\n{BEGIN:subscr_notifications user_id asciiversion}\n{!date \'{site_date_format} {site_time_format}\' \'{note_created}\'!}\n  {note_message}\n  {note_url}\n  {note_body:nohtml}\n{END:subscr_notifications}\n\n-----------------------------------------------------------------------------\n\nThis message is sent from {site_name}\n{site_url}\nYou can edit your subscriptions at {site_url}/subscriptions.php?id={user_id}');
INSERT INTO templates VALUES (NULL,'2003-06-28 18:54:46','2003-06-28 18:54:46',0,0,'month','','{!# $Id: templates.sql,v 1.37 2003/07/06 12:50:43 glen Exp $\n### generates (hopefully) a calendar of events\n#!}\n{!set no_title \'Yes\'!}\n<table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"100%\">\n<tr><td>\n&lt;&lt;<a href=\"{site_path}/month.php?y={prev_year}&amp;m={prev_month}\">{!date\n    \'F Y\' \'{prev_year}-{prev_month}-1\'!}</a>\n</td>\n<td align=\"center\">\n<h2>{month} {year}</h2>\n</td>\n<td align=\"right\">\n<a href=\"{site_path}/month.php?y={next_year}&amp;m={next_month}\">{!date\n    \'F Y\' \'{next_year}-{next_month}-1\'!}</a>&gt;&gt;\n</td>\n</tr></table>\n<table class=\"calendar\" width=\"100%\" align=\"center\">\n<tr>\n    <th width=\"12%\">Sunday</th>\n    <th width=\"15%\">Monday</th>\n    <th width=\"15%\">Tuesday</th>\n    <th width=\"15%\">Wednesday</th>\n    <th width=\"15%\">Thursday</th>\n    <th width=\"15%\">Friday</th>\n    <th width=\"12%\">Saturday</th>\n</tr>\n{BEGIN:month_days yearmon}\n{!if \'({mday}==1)&&({wday}!=0)\'\n    \'<td colspan=\"{wday}\">&nbsp;</td>\'\n!}\n{!if \'{wday}==0\'\n    \'<tr>\'\n!}\n<td class=\"day\"><p class=\"calday\">\n    {!if \'(({mday}+0)==({thisday}+0))&&(({mon}+0)==({thismonth}+0))&&({year}=={thisyear})\'\n        \'<span class=\"today\">{mday}</span>\'\n        \'{mday}\'\n    !}</p>\n    <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"100%\">\n    {BEGIN:day_events}\n    <tr>\n    <td style=\"font-size:9px;\">{!if \'{hours}\' \'{hours:%02d}:{minutes:%02d}\'!}\n        <a href=\"{site_path}/document.php?id={doc_id}\">{doc_title}</a>\n    </td></tr>\n    {END:day_events}\n    </table>\n{!if \'{wday}==6\'\n    \'</tr>\'\n!}\n{END:month_days}\n{!if \'{wday}!=6\'\n    \'</tr>\'\n!}\n</table>');
INSERT INTO templates VALUES (NULL,'2003-06-28 18:54:46','2003-06-28 18:54:46',0,0,'link','','<p>Link: <a href=\"{doc_link_url}\">{doc_link_url}</a></p>\n<p>{doc_body}</p>\n<p class=\"info\">\n    <a href=\"{site_path}/user.php?id={doc_user_id}\">{doc_user_name}</a>\n    {!if defined(doc_folder_name)\n        \'| <a href=\"{site_path}/folder.php?id={doc_folder_id}\">{doc_folder_name}</a>\'\n    !}\n    | {!date \'{site_date_format}\' \'{doc_created}\'!}\n</p>\n{template:comment}');
INSERT INTO templates VALUES (NULL,'2003-06-28 18:54:46','2003-06-28 18:54:46',0,0,'macros','','{!# Siteframe Macro Library\n### Copyright (c)2001, Broadpool, LLC. All rights reserved.\n#!}\n{!set macro_library_version \'$Id: templates.sql,v 1.37 2003/07/06 12:50:43 glen Exp $\'!}\n{!# ---------------------------------------------------------------------------\n### user - generates a hyperlinked reference to a user\n### syntax:\n###   !user N\n### example:\n###   !user 371\n#!}\n{!define user\n    \'{BEGIN:user $1 user$1}<a href=\"{site_path}/user.php?id={user_user_id}\"\n    >{user_user_name}</a>{END:user}\'\n!}\n{!# ---------------------------------------------------------------------------\n### doclink - generates a hyperlinked reference to a document\n### n.b. name changed from \'document\', which is an internal macro that inserts\n### the entire contents of a document at the macro location\n### syntax:\n###   !doclink N\n### example:\n###   !doclink 132\n#!}\n{!define doclink\n    \'{BEGIN:document $1 doc$1}<a href=\"{site_path}/document.php?id={doc_id}\"\n    >{doc_title}</a>{END:document}\'\n!}\n{!# ---------------------------------------------------------------------------\n### folder - generates a hyperlinked reference to a folder\n### syntax:\n###   !folder N\n### example:\n###   !folder 132\n#!}\n{!define folder\n    \'{BEGIN:folder $1 folder$1}<a href=\"{site_path}/folder.php?id={folder_id}\"\n    >{folder_name_display}</a>{END:folder}\'\n!}\n{!# ---------------------------------------------------------------------------\n### thumbnail - generates a clickable thumbnail\n### syntax:\n###   !thumbnail doc_id [options]\n### example:\n###   !thumbnail 242 \'align=\"right\"\'\n### if you want thumbnails to be a different size, then change\n### {doc_file_150} to reference a different thumbnail size\n#!}\n{!define thumbnail\n    \'{BEGIN:document $1 thumb$1}\n     <a href=\"{site_path}/document.php?id={doc_id}\"\n     ><img src=\"{doc_file_150}\" alt=\"{doc_title}\" border=\"0\"\n          title=\"{doc_title}\" $2\n     /></a>\n     {END:document}\'\n!}\n{!# ---------------------------------------------------------------------------\n### link - generates a hyperlink\n### syntax:\n###   !link text href\n### example:\n###   !link \'The Contax G Pages\' \'http://www.contaxg.com\'\n#!}\n{!define link\n    \'<a href=\"$2\">$1</a>\'\n!}\n{!# ---------------------------------------------------------------------------\n### bookmark - similar to the above, but generates a named bookmark\n### syntax:\n###   !bookmark \'name\'\n#!}\n{!define bookmark\n    \'<a name=\"$1\"></a>\'\n!}\n{!# ---------------------------------------------------------------------------\n### icon definitions\n#!}\n{!define icon\n    \'{site_path}/images/$1.gif\'\n!}\n{!# ---------------------------------------------------------------------------\n### powered_by_siteframe\n### generates a link to the \"Powered by Siteframe\" image\n###\n### powered_by_siteframe_graphic\n### generates a link with a graphic image\n#!}\n{!define powered_by_siteframe\n    \'<a href=\"http://siteframe.org\">Powered by Siteframe</a>\'\n!}\n{!define powered_by_siteframe_graphic\n    \'<div $1><a href=\"http://siteframe.org\"\n    ><img src=\"http://siteframe.org/siteframepower.gif\"\n          alt=\"Powered by Siteframe\"\n          title=\"a .COMmunity powered by Siteframe\"\n    /></a></div>\'\n!}\n{!# ---------------------------------------------------------------------------\n#!}\n{!set siteframe_credits\n    \'Many thanks to the following people whose contributions helped to make\n     this a great product: Richard Sintchak, Knut Skjaerven, Lincoln Michaud,\n     Charez Golvalez,\n     Vish Vishvanath, Anita Campbell, Ted Campbell, Ted Pecot,\n     Dennis Adams, Barbara Adams, Bob Cousins, Steve Moorey, Christopher Chen,\n     and Rich Preston &bull; Thanks to Nikon for making great cameras &bull;\n     Thanks to Contax for making great cameras and for inspiring this work\n     &bull; Special thanks to Elam for teaching me that baseball is more\n     important than software &bull; Mountain Dew / Code Red &bull; FreeBSD\n     &bull; SuSE Linux\'\n!}');
INSERT INTO templates VALUES (NULL,'2003-06-28 18:54:46','2003-06-28 18:54:46',0,0,'inlcomment','','{!# $Id: templates.sql,v 1.37 2003/07/06 12:50:43 glen Exp $\n### Copyright (c)2001-2003, Broadpool, LLC. All rights reserved. See LICENSE.txt for details.\n### this file defines an inline form that can be used\n### for adding comments to a document !}\n{!if defined(user_id)\n\'<form method=\"post\" \n       action=\"{site_path}/comment.php\" \n       enctype=\"multipart/form-data\" >\n    <p class=\"prompt\">Subject:</p>\n    <p class=\"field\">\n        <input type=\"text\" name=\"comment_subject\" />\n    </p>\n    <p class=\"prompt\">Comment:</p>\n    <p class=\"field\">\n        <textarea name=\"comment_body\" class=\"inputbox\" rows=\"5\" cols=\"50\"></textarea>\n    </p>\n    <p class=\"prompt\">Send comment to:</p>\n    <input type=\"hidden\" name=\"send_email\" value=\"1\"/>\n    <input type=\"hidden\" name=\"submitted\" value=\"1\"/>\n    <input type=\"hidden\" name=\"comment_reply_to\" value=\"\"/>\n    <input type=\"hidden\" name=\"comment_owner_id\" value=\"{user_id}\"/>\n    <input type=\"hidden\" name=\"comment_doc_id\" value=\"{doc_id}\"/>\n    <input type=\"submit\" value=\"Submit\"/>\n    <input type=\"reset\"/>\n</form>\'\n!}');
INSERT INTO templates VALUES (NULL,'2003-06-28 18:54:46','2003-06-28 18:54:46',0,1,'index','','{!# generic index.php template - weblog format !}\n{!set no_title TRUE!} {!# this turns off the front page title !}\n<img src=\"{site_path}/images/siteframepower.gif\" alt=\"Siteframe {siteframe_version}\" align=\"right\"/>\n\n<h1>Welcome to Siteframe {siteframe_version}</h1>\n<p>This is the default home page for a \n<a href=\"http://siteframe.org\">Siteframe</a>-powered website. \nYou should replace this with a home page of your own choosing, \nor you can modify this page to suit your needs.</p>\n<h2>Changing the home page</h2>\n<p>To edit this page, navigate to \n<a href=\"{site_path}/admin/\">Control Panel</a> \n&rarr; Maintain content templates, and click on \"index\".</p>\n<p>To select another home page template, navigate to \n<a href=\"{site_path}/admin/\">Control Panel</a> \n&rarr; Extended properties, and choose another template \nfrom the drop-down menu by HOME_PAGE.</p>\n<p>To create your own home page template:</p>\n<ol>\n<li>Navigate to <a href=\"{site_path}/admin/\">Control Panel</a> \n&rarr; Maintain content templates.</li>\n<li>Select \"Create a new template.\"</li>\n<li>Set the template type to \"Home Page\" and enter any desired \nhome page text, then press Save.</li>\n<li>Go to <a href=\"{site_path}/admin/\">Control Panel</a> \n&rarr; Extended properties, then choose your new template \nfrom the HOME_PAGE drop-down list.</li>\n</ol>\n<p>It is recommend that you do <em>not</em> modify one of the\nexisting templates, since your changes may be lost in a future software\nupgrade. Either use an existing template unchanged, or create a copy of\nan existing template to modify.</p>\n\n<h2>Administrative user account</h2>\n<p>During the installation process, you should have created at least one\nadministrative user account. If you did not, you will be unable to maintain\nand manage this website.</p>\n<p>To create a new administrative user,\n<a href=\"{site_path}/admin/newadmin.php\">click here</a>.</p>\n<p>If your site already has an administrative user, you are required to\nlogin before using the Control Panel.</a>\n\n<h2>Configuring your site</h2>\n<p>If you\'re seeing this page, then you have already completed at least the \nfirst stage in your site configuration. You can also visit the \n<a href=\"{site_path}/admin/properties.php\">Extended properties</a>\npage for other configuration options.</p>\n\n<h2>Site Statistics</h2>\n<p>The <a href=\"{site_path}/admin/\">Control Panel</a> contains\nlinks to a number of useful reports about your site, including the numbers\nof unique visitors per day, lists of referrers, and the most recent visitors\nto your site.</p>\n\n<h2>Further support</h2>\n<p>More documentation is available at the Siteframe website\n(<a href=\"http://siteframe.org\">http://siteframe.org</a>). \nIn addition, there are a number of mailing lists that Siteframe administrators\nuse to assist each other with configuring and maintaining their sites.\nYou can find more information about these at the Siteframe website.</p>\n<p>If you have specific problems or questions, you can contact Siteframe\ntechnical support at:</p>\n<blockquote><a href=\"mailto:support@siteframe.org\">support@siteframe.org</a></blockquote>\n<p>You can submit feedback to the Siteframe development team via your\n<a href=\"{site_path}/admin/feedback.php\">Control Panel feedback page</a>.\n</p>');
INSERT INTO templates VALUES (NULL,'2003-06-28 18:54:46','2003-06-28 18:54:46',0,0,'image','','{!# special head_content - this defines styles used for the close box\n    as well as the JavaScript necessary to maintain the cookie which\n    indicates whether the imageinfo box is hidden or displayed\n#!}\n{!set head_content \'\n<style type=\"text/css\">\n.xbutton {\nfloat:right;\nheight:13px;\nwidth:12px;\nfont-family:arial,helvetica,sans-serif;\nfont-size:12px;\nfont-weight:bold;\ntext-align:center;\nborder-top:1px silver solid;\nborder-left:1px silver solid;\nborder-right:1px black solid;\nborder-bottom:1px black solid;\nbackground:gray;\ncolor:silver;\n}\n.xbutton a,.xbutton a:visited {\ncolor:silver;\ntext-decoration:none;\n}\n.xbutton a:hover {\ntext-decoration:none;\ncolor:white;\n}\n.xbutton a:active {\ncolor:black;\n}\n</style>\n<script language=\"JavaScript\">\nfunction setCookie(name,value)\n{\n  var nextyear = new Date();\n  nextyear.setFullYear(nextyear.getFullYear()+1);\n  document.cookie = escape(name)+\"=\"+escape(value)+\";path={site_path}/;expires=\"+nextyear.toGMTString();\n}\n</script>\' 1!}\n\n\n<div class=\"backnext\">\n<table>\n<tr><td width=\"50%\" align=\"right\">\n{!if defined(prev_id)\n    \'<b><a href=\"{site_path}/document.php?id={prev_id}\">&larr;<span>Back</span></a></b>&nbsp;\'\n    \'&nbsp;\'\n!}\n</td><td>\n{!if defined(next_id)\n  \'&nbsp;<b><a href=\"{site_path}/document.php?id={next_id}\"><span>Next</span>&rarr;</a></b>\'\n  \'&nbsp;\'\n!}\n</td></tr></table>\n</div>\n<div class=\"image\">\n    <p class=\"imageframe\">\n    {!if defined(image_full_size)\n        \'<img src=\"{doc_file}\" border=\"0\" alt=\"{doc_title}\" title=\"{doc_title}\"/>\'\n        \'<a href=\"{site_path}/document.php?id={doc_id}&full=1\"><img {doc_image_default} border=\"0\"\n            alt=\"{doc_title}\" title=\"{doc_title}\"/></a>\'\n    !}\n    <br/>\n    Copyright &copy;{!date \'Y\' \'{doc_created}\'!}, {!if defined(image_copyright) \'{image_copyright}\' \'{doc_user_name}\'!}\n    </p>\n    <div class=\"description\">\n    {doc_body}\n    </div>\n</div>\n\n<div class=\"infotablewrapper\">\n<table class=\"infotable\" {!if \'{_COOKIE_hideinfo}+0\' \'style=\"display:none;\"\'!}>\n    <tr><th>Photographer: </th>\n        <td>\n        {!if \'{_COOKIE_hideinfo}+0\'\n        \'\'\n        \'<div class=\"xbutton\"><a href=\"{site_path}/document.php?id={doc_id}\"\n            title=\"Click to hide image information box\"\n            onClick=\"setCookie(&#34;hideinfo&#34;,1);\">X</a></div>\'\n        !}\n        <a href=\"{site_path}/user.php?id={doc_user_id}\">{doc_user_name}</a>\n        {!user_indicators doc!}\n        </td>\n        </tr>\n    <tr><th>Folder: </th>\n        <td>{!if \'{doc_folder_id}\'\n        \'<a href=\"{site_path}/folder.php?id={doc_folder_id}\">{doc_folder_name}</a>\'\n        !} </td>\n        </tr>\n    <tr><th>Uploaded: </th>\n        <td>{!date \'{site_date_format} {site_time_format}\' \'{doc_created}\'!} </td>\n        </tr>\n{!if \'({allow_ratings}+0)&&(iseditor({doc_user_id})||{doc_rating_count}>4)\'\n    \'<tr><th>Current Rating: </th>\n     <td>{doc_rating:%.2f}/{doc_rating_count:%d}<br/>\n     <a href=\"{site_path}/ratings.php?id={doc_id}\">View all ratings</a><br/>\n     <a href=\"{site_path}/deleterating.php?id={doc_id}\">Delete my rating</a>\n     </td></tr>\'\n!}\n{!if defined(doc_share_count)\n    \'<tr><th>Sharing: </th>\n     <td>{doc_share_count:%d} time(s).\n     </td></tr>\'\n!}\n    <tr><th>Copying allowed: </th>\n        <td>{!if \'{copying_allowed}+0\' \'Yes\' \'No\'!}\n            {!if \'{copying_allowed}+0\'\n                \'<a href=\"#\" onClick=\"document.sflyc4p.submit();return false;\">\n                    Print this photo on Shutterfly</a>\'\n            !}\n        </td>\n        </tr>\n    <tr><th>Model release available: </th>\n        <td>{!if \'{image_model_release}+0\' \'Yes\' \'No\'!}</td>\n        </tr>\n    <tr><th>Camera: </th>\n        <td>{image_camera}</td>\n        </tr>\n    <tr><th>Lens: </th>\n        <td>{image_lens}</td>\n        </tr>\n    <tr><th>Film: </th>\n        <td>{image_film}</td>\n        </tr>\n    <tr><th>Exposure: </th>\n        <td>{image_exposure}</td>\n        </tr>\n    <tr><th>Flash: </th>\n        <td>{!if \'{image_flash}+0\' \'Yes\' \'No\'!}</td>\n        </tr>\n    <tr><th>Support: </th>\n        <td>{image_support}</td>\n        </tr>\n    <tr><th>Filtration: </th>\n        <td>{image_filter}</td>\n        </tr>\n    <tr><th>Adjustments (digital/darkroom): </th>\n        <td>{image_adjustment}</td>\n        </tr>\n    <tr><th>Scan resolution: </th>\n        <td>{image_height}x{image_width}</td>\n        </tr>\n</table>\n</div>\n\n{template:comment}\n\n{!if \'{copying_allowed}+0\'\n    \'<form id=\"shutterfly\" name=\"sflyc4p\" action=\"http://www.shutterfly.com/c4p/UpdateCart.jsp\" method=\"post\">\n      <input type=\"hidden\" name=\"addim\" value=\"1\">\n      <input type=\"hidden\" name=\"protocol\" value=\"SFP,100\">\n      <input type=\"hidden\" name=\"pid\" value=\"C4P\">\n      <input type=\"hidden\" name=\"psid\" value=\"AFFL\">\n      <input type=\"hidden\" name=\"referid\" value=\"a2iXxMnVb\">\n      <input type=\"hidden\" name=\"offerid\" value=\"34049\">\n      <input type=\"hidden\" name=\"returl\" value=\"{site_url}/document.php?id={doc_id}\">\n      <input type=\"hidden\" name=\"imraw-1\" value=\"{site_url}/{doc_file}\">\n      <input type=\"hidden\" name=\"imrawheight-1\" value=\"{image_height}\">\n      <input type=\"hidden\" name=\"imrawwidth-1\" value=\"{image_width}\">\n      <input type=\"hidden\" name=\"imthumb-1\" value=\"{site_url}/{doc_file_150}\">\n      <input type=\"hidden\" name=\"imbkprntb-1\" value=\"Hi\">\n    </form>\'\n!}');
INSERT INTO templates VALUES (NULL,'2003-06-28 18:54:46','2003-06-30 23:14:20',0,1,'Home Page - Weblog','','{!# generic index.php template - weblog format !}\r\n{!set no_title TRUE!} {!# this turns off the front page title !}\r\n\r\n<div id=\"weblog\">\r\n{BEGIN:doc_days 14}\r\n<h2 class=\"date\">{!date \'{site_date_format}\' \'{day}\'!}</h2>\r\n\r\n  {BEGIN:day_docs day}\r\n  <h3 class=\"title\">{doc_title}</h3>\r\n  {!case \'{doc_type}\'\r\n\r\n    \'Image\'   \'<a href=\"{site_path}/document.php?id={doc_id}\"\r\n              ><img src=\"{doc_file_400}\" alt=\"{doc_title}\"/></a>\'\r\n\r\n    \'Poll\'    \'{poll_display}\'\r\n\r\n    \'Link\'    \'Link: <a href=\"{doc_link_url}\">{doc_link_url}</a>\'\r\n\r\n  !}\r\n  {!if defined(doc_summary)\r\n    \'{doc_summary}\r\n     <p>&raquo;&nbsp;<a href=\"{site_path}/document.php?id={doc_id}\">Click \r\n     here for entire document</a></p>\'\r\n    \'{doc_body}\'\r\n  !}\r\n  <p class=\"info\">\r\n  Posted by {!user \'{doc_user_id}\'!}\r\n  at {!date \'{site_time_format}\' \'{doc_created}\'!}\r\n  {!if \'{doc_folder_id}\'\r\n    \'in <a href=\"{site_path}/folder.php?id={doc_folder_id}\">{doc_folder_name}</a>\'\r\n  !}\r\n  {sep}\r\n  <a href=\"{site_path}/document.php?id={doc_id}\">permanent link</a>\r\n  {!if \'{allow_comments}+0\'\r\n    \'{sep} <a href=\"#\" onClick=\"window.open(&#34;{site_path}/comments.php?id={doc_id}&#34;, &#34comments&#34;, &#34;height=500,width=350,scrollbars=yes&#34;);\">{doc_comment_count:%d} comment(s)</a>\'\r\n  !}\r\n  </p>\r\n  {END:day_docs}\r\n\r\n{END:doc_days}\r\n</div><!-- weblog -->\r\n\r\n<a href=\"http://siteframe.org\"><img src=\"{site_path}/images/siteframepower.gif\" alt=\"Siteframe {siteframe_version}\"/></a>');
INSERT INTO templates VALUES (NULL,'2003-06-28 18:54:46','2003-06-28 18:54:46',0,0,'groups','','<!-- displays all groups -->\n\n<table class=\"list\">\n<tr><th>Group Name </th><th>Group Owner </th><th>Created </th></tr>\n{BEGIN:all_groups}\n<tr><td><a href=\"{site_path}/group.php?id={group_id}\"><b>{group_name}</b></a>\n{group_body}</td>\n<td><a href=\"{site_path}/user.php?id={group_owner_id}\">{group_user_name}</a></td>\n<td>{!date \'{site_date_format}\' \'{group_created}\'!}</td>\n</tr>\n{END:all_groups}\n</table>\n\n{!if defined(user_id)\n \'<p class=\"action\"><a href=\"{site_path}/editgroup.php\">Create a new group</a></p>\'\n!}');
