INSERT INTO templates VALUES (NULL,'2003-06-28 18:54:46','2003-06-30 22:56:32',0,0,'comment','','{!# $Id: templates.sql,v 1.37 2003/07/06 12:50:43 glen Exp $ !}\r\n{!#if \'({allow_ratings}+0)&&(iseditor({doc_user_id})||{doc_rating_count}>4)\'\r\n    \'<p class=\"rating\">Current rating: {doc_rating}/{doc_rating_count} &bull;\r\n     <a href=\"{site_path}/ratings.php?id={doc_id}\">View all ratings</a> &bull;\r\n     <a href=\"{site_path}/deleterating.php?id={doc_id}\">Delete my rating</a></p>\'\r\n!}\r\n{!#if defined(doc_share_count)\r\n    \'<p class=\"info\">This {doc_type} has been shared {doc_share_count} time(s).</p>\'\r\n!}\r\n<p class=\"action\">\r\n{!if \'{_COOKIE_hideinfo}+0&&(\"{doc_type}\"==\"Image\")\'\r\n  \'<a href=\"{site_path}/document.php?id={doc_id}\"\r\n      title=\"Show complete {doc_type_display} information\"\r\n      onClick=\"setCookie(&#34;hideinfo&#34;,0);\">{doc_type_display} Information</a> {sep}\'\r\n!}\r\n{!if \'({allow_comments}+0)&&({comment_inline}+0)\'\r\n    \'<a href=\"{site_path}/comment.php?id={doc_id}\"\r\n        title=\"Add rating/comment to this {doc_type_display}\">Comment/Rate</a> {sep}\'\r\n!}\r\n{!if \'({allow_comments}+0)&&(!{comment_inline}+0)\'\r\n   \'<a href=\"#\"\r\n    title=\"Add rating/comment to this {doc_type_display}\"\r\n    onClick=\"javascript:var x=screen.availWidth-420;\r\n            var f=&#34;left=&#34;+x+&#34;,top=20,width=400,height=500,toolbar=no,scrollbars=yes,status=no,copyhistory=no&#34;\r\n            window.open(&#34;{site_path}/comment.php?id={doc_id}&#34;,\r\n            &#34;comment_window&#34;,f)\">\r\n    Comment/Rate</a> {sep}\'\r\n!}\r\n<a href=\"{site_path}/email.php?id={doc_id}\"\r\n   title=\"Send an email to someone about this {doc_type_display}\">Share this {doc_type_display}</a>\r\n{!if \'{subscription_enable}\'\r\n  \'{sep} <a href=\"{site_path}/subscribe.php?type=D&amp;obj={doc_id}\">Subscribe</a>\'\r\n!}\r\n{!if editor(doc_owner_id)\r\n    \'{sep} <a href=\"{site_path}/edit.php